import {Component} from 'react';
import Keyboard from 'react-virtual-keyboard';

class Home extends Component {

    constructor(props){
        super(props);
        this.state = {
            word : "",
            input : null
        }
        this.handleChange = this.handleChange.bind(this);
        //hdhjthis.handleSubmit = this.handleSubmit.bind(this);
    }

    /*componentDidMount() {
        this.setState({field1:''});
    }
    handleSubmit(event) {
        this.setState({'field1': event.target.elements.word.value});
    }*/
    handleChange(event) {
        this.setState({word:event.target.value});
    }
    onInputChanged = (data) => {
        this.setState({ input: data });
    }
     
    onInputSubmitted = (data) => {
    console.log("Input submitted:", data);
    }


    render() {
        return(
            <div>
                <form>
                <input type="text" placeholder="word" name = "word" onChange={this.handleChange}></input>
                </form>
                
                { this.state.word !== ""  ? <h1>Hi {this.state.word}</h1> : "" }
                <Keyboard 
                    value={this.state.input}
                    name='keyboard'
                    options={{
                        type:"input",
                        layout: "qwerty",
                        alwaysOpen: true,
                        usePreview: false,
                        useWheel: false,
                        language:'Tamil',
                        stickyShift: false,
                        appendLocally: true,
                        color: "light",
                        updateOnChange: true,
                        initialFocus: true,
                        display: {
                        "accept" : "Submit"
                        }
                    }}
                    onChange={this.onInputChanged}
                    onAccepted={this.onInputSubmitted}
                    ref={k => this.keyboard = k}
                    />
            </div>
        )
    }
}

export default Home;
