import React, { Component } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

class Speechinput extends Component {
    constructor(props) {
        super(props);
        state = {
            speaking : null,
            recognizing: null,
            final_transcript: '',
            recognition : new webkitSpeechRecognition(),

        }
        this.startButton = this.startButton.bind(this);
    }

    componentDidMount() {
        this.setState({'speaking':false});
    }

    startButton() {
        const is_listening = this.state.speaking;
        if (is_listening) {
            this.state.recognition.stop();
            return;
          }
        this.setState({'final_transcript':'',
            'recognition.lang':'ta-IN',
            'recognition.continuous' :true,
            'recognition.interimResults' :true
        });
        this.state.recognition.start();
    }

    render() {
        return(
            <>
                {this.state.speaking ===  true ? "you are speaking..." : "Click button speak to speak"}
                <button type="button" onClick={this.startButton}> {this.state.speaking ? Stop : Speak} </button>
            </>
        )
    }
}
export default Speechinput;
