import React, {Component} from 'react';

class Speechinput extends Component {

    constructor(props) {
        super(props);
        state = {
            'speaking': null,
        }
    }

    componentDidMount(){
        this.setState({'speaking':false});
    }

    render() {
        return(
            <button type="button"> {this.state.speaking === true ? "Stop" : "Speak"} </button>
        );
    }
}
