import React, {Component} from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

class Speechinput extends Component {

    constructor(props) {
        super(props);
        this.state = {
            speaking: null,
        }
        this.buttonclicked = this.buttonclicked.bind(this);
    }

    componentDidMount(){
        this.setState({'speaking':false});
    }

    buttonclicked() {
        const status = this.state.speaking;
        this.setState({"speaking":!status});
        if (status === false) SpeechRecognition.startListening();
        else SpeechRecognition.abortListening();
    }

    render() {
        return(
            <button type="button" onClick={this.buttonclicked}> {this.state.speaking === true ? "Stop" : "Speak"} </button>
        );
    }
}  

export default Speechinput;
