import React, { Component } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

var recognition = new window.webkitSpeechRecognition();
var final_transcript = '';
var interim_transcript = ''; 

recognition.onresult = function(event) {
    var interim_transcript = '';
    if (typeof(event.results) == 'undefined') {
        recognition.onend = null;
        recognition.stop();
        upgrade();
        return;
    }
    for (var i = event.resultIndex; i < event.results.length; ++i) {
        if (event.results[i].isFinal) {
        final_transcript += event.results[i][0].transcript;
        } else {
        interim_transcript += event.results[i][0].transcript;
        }
    }
};

class Speechinput extends Component {
    constructor(props) {
        super(props);
        this.state = {
            speaking : null,
            recognizing: null,
            final_transcript: '',
            recognition : null

        }
        this.startButton = this.startButton.bind(this);
        //this.setConfign = this.setConfign.bind(this);
    }

    async componentDidMount() {
        await this.setState({'speaking':false});
        recognition.lang='ta-IN';
        recognition.continuous =true;
        recognition.interimResults=true;
    }

    startButton() {
        const is_listening = this.state.speaking;
        if (is_listening) {
            recognition.stop();
            return;
        }
        recognition.start();
    }

    render() {
        return(
            <>
                {this.state.speaking ===  true ? "you are speaking..." : "Click button speak to speak"}
                <button type="button" onClick={this.startButton}> {this.state.speaking ? "Stop" : "Speak"} </button>
            </>
        )
    }
}
export default Speechinput;
